import 'package:flutter/material.dart';

class ProductListScreen extends StatefulWidget {
  const ProductListScreen({super.key});

  @override
  _ProductListScreenState createState() => _ProductListScreenState();
}

class _ProductListScreenState extends State<ProductListScreen> {
  final List<Map<String, dynamic>> products = [
    {
      "id": 1,
      "name": "Leche deslactosada",
      "status": "Activo",
      "category": "Lácteos",
      "price": 4200,
      "image":
          "data:image/jpeg;base64,",
    },
    {
      "id": 2,
      "name": "Pan integral",
      "status": "Inactivo",
      "category": "Panadería",
      "price": 2500,
      "image":
          "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTWJr8X1V6aMzslBBNiC9EH57y7-ZUtpcv57Q&s",
    },
    {
      "id": 3,
      "name": "Queso campesino",
      "status": "Activo",
      "category": "Lácteos",
      "price": 7500,
      "image":
          "data:image/jpeg;base64,",
    },
    {
      "id": 4,
      "name": "Yogur natural",
      "status": "Activo",
      "category": "Lácteos",
      "price": 3000,
      "image":
          "data:image/jpeg;base64,",
    },
    {
      "id": 5,
      "name": "Mantequilla sin sal",
      "status": "Inactivo",
      "category": "Lácteos",
      "price": 6800,
      "image":
          "data:image/jpeg;base64,",
    },
    {
      "id": 6,
      "name": "Cereal integral",
      "status": "Activo",
      "category": "Cereales",
      "price": 5400,
      "image":
          "data:image/jpeg;base64,",
    },
    {
      "id": 7,
      "name": "Huevos orgánicos",
      "status": "Activo",
      "category": "Huevos",
      "price": 12000,
      "image":
          "data:image/jpeg;base64,",
    },
    {
      "id": 8,
      "name": "Jugo de naranja",
      "status": "Inactivo",
      "category": "Bebidas",
      "price": 4500,
      "image":
          "data:image/jpeg;base64,",
    },
    {
      "id": 9,
      "name": "Café molido",
      "status": "Activo",
      "category": "Bebidas",
      "price": 15000,
      "image":
          "data:image/jpeg;base64,",
    },
    {
      "id": 10,
      "name": "Té verde",
      "status": "Activo",
      "category": "Bebidas",
      "price": 8000,
      "image":
          "data:image/jpeg;base64,",
    },
  ];
  String searchQuery = "";
  String sortOption = "Relevancia";
  String statusFilter = "Todos";
  bool isPriceAsc = true; // 🔹 controla la dirección del orden de precio

  @override
  Widget build(BuildContext context) {
    // Filtrar productos
    List<Map<String, dynamic>> filteredProducts = products.where((product) {
      final matchesSearch =
          product["name"].toLowerCase().contains(searchQuery.toLowerCase()) ||
          product["category"].toLowerCase().contains(searchQuery.toLowerCase());

      final matchesStatus =
          statusFilter == "Todos" ||
          product["status"].toLowerCase() == statusFilter.toLowerCase();

      return matchesSearch && matchesStatus;
    }).toList();

    // 🔹 Ordenar 
    if (sortOption == "precio") {
      filteredProducts.sort((a, b) {
        final aPrice = double.parse(a["price"].toString());
        final bPrice = double.parse(b["price"].toString());
        return isPriceAsc ? aPrice.compareTo(bPrice) : bPrice.compareTo(aPrice);
      });
    }

    return Scaffold(
      appBar: AppBar(
        backgroundColor: const Color.fromARGB(136, 135, 234, 129),
        title: Container(
          height: 40,
          decoration: BoxDecoration(
            color: Colors.white,
            borderRadius: BorderRadius.circular(20),
          ),
          child: TextField(
            onChanged: (value) {
              setState(() {
                searchQuery = value;
              });
            },
            decoration: InputDecoration(
              hintText: "Buscar en Kajamart...",
              prefixIcon: Icon(Icons.search, color: Colors.grey),
              border: InputBorder.none,
              contentPadding: EdgeInsets.symmetric(vertical: 10),
            ),
          ),
        ),
      ),
      body: Column(
        children: [
          // 🔹 Barra de filtros 
          TweenAnimationBuilder(
            duration: Duration(milliseconds: 500),
            curve: Curves.easeOut,
            tween: Tween<double>(begin: -50, end: 0),
            builder: (context, double value, child) {
              return Transform.translate(
                offset: Offset(0, value),
                child: Opacity(opacity: 1 - (value.abs() / 50), child: child),
              );
            },
            child: Container(
              padding: EdgeInsets.symmetric(horizontal: 8, vertical: 8),
              decoration: BoxDecoration(
                color: Colors.white,
                boxShadow: [BoxShadow(color: Colors.black12, blurRadius: 5)],
              ),
              child: Row(
                mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                children: [
                  Expanded(child: buildChip("Relevancia", isSort: true)),
                  SizedBox(width: 6),
                  Expanded(child: buildPriceChip()),
                  SizedBox(width: 6),
                  Expanded(child: buildChip("Activo", isSort: false)),
                  SizedBox(width: 6),
                  Expanded(child: buildChip("Inactivo", isSort: false)),
                ],
              ),
            ),
          ),

          // 🔹 Lista de productos 
          Expanded(
            child: AnimatedSwitcher(
              duration: Duration(milliseconds: 400),
              switchInCurve: Curves.easeIn,
              switchOutCurve: Curves.easeOut,
              child: GridView.builder(
                key: ValueKey(filteredProducts.length),
                padding: EdgeInsets.all(8),
                gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                  crossAxisCount: 2,
                  crossAxisSpacing: 8,
                  mainAxisSpacing: 8,
                  childAspectRatio: 0.6,
                ),
                itemCount: filteredProducts.length,
                itemBuilder: (context, index) {
                  final product = filteredProducts[index];
                  return TweenAnimationBuilder(
                    tween: Tween<double>(begin: 0, end: 1),
                    duration: Duration(milliseconds: 300 + (index * 100)),
                    curve: Curves.easeOut,
                    builder: (context, double value, child) {
                      return Opacity(
                        opacity: value,
                        child: Transform.translate(
                          offset: Offset(0, 40 * (1 - value)),
                          child: child,
                        ),
                      );
                    },
                    child: Container(
                      decoration: BoxDecoration(
                        color: Colors.white,
                        borderRadius: BorderRadius.circular(10),
                        boxShadow: [
                          BoxShadow(
                            color: Colors.black12,
                            blurRadius: 4,
                            offset: Offset(0, 2),
                          ),
                        ],
                      ),
                      child: Column(
                        crossAxisAlignment: CrossAxisAlignment.stretch,
                        children: [
                          Expanded(
                            child: ClipRRect(
                              borderRadius: BorderRadius.vertical(
                                top: Radius.circular(10),
                              ),
                              child: Image.network(
                                product["image"],
                                fit: BoxFit.cover,
                              ),
                            ),
                          ),
                          Padding(
                            padding: EdgeInsets.all(6),
                            child: Column(
                              crossAxisAlignment: CrossAxisAlignment.start,
                              children: [
                                Text(
                                  product["name"],
                                  style: TextStyle(
                                    fontWeight: FontWeight.bold,
                                    fontSize: 13,
                                  ),
                                  maxLines: 2,
                                  overflow: TextOverflow.ellipsis,
                                ),
                                SizedBox(height: 4),
                                Text(
                                  "\$${product["price"]}",
                                  style: TextStyle(
                                    color: const Color.fromARGB(255, 0, 0, 0),
                                    fontSize: 16,
                                    fontWeight: FontWeight.bold,
                                  ),
                                ),
                                SizedBox(height: 2),
                                Text(
                                  product["status"],
                                  style: TextStyle(
                                    fontSize: 12,
                                    color: product["status"] == "Activo"
                                        ? Colors.green
                                        : Colors.red,
                                  ),
                                ),
                              ],
                            ),
                          ),
                        ],
                      ),
                    ),
                  );
                },
              ),
            ),
          ),
        ],
      ),
    );
  }

  Widget buildChip(String label, {bool isSort = true}) {
    bool isSelected = isSort
        ? sortOption == label.toLowerCase()
        : statusFilter.toLowerCase() == label.toLowerCase();

    return FilterChip(
      label: Center(child: Text(label)),
      selected: isSelected,
      onSelected: (selected) {
        setState(() {
          if (isSort) {
            sortOption = label.toLowerCase();
          } else {
            statusFilter = isSelected ? "Todos" : label;
          }
        });
      },
      backgroundColor: Colors.grey.shade200,
      selectedColor: Colors.green.shade100,
      checkmarkColor: Colors.green,
      labelStyle: TextStyle(fontWeight: FontWeight.w500),
      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(20)),
    );
  }

  Widget buildPriceChip() {
    bool isSelected = sortOption == "precio";

    return FilterChip(
      label: Row(
        mainAxisAlignment: MainAxisAlignment.center,
        mainAxisSize: MainAxisSize.max,
        children: [
          Text("Precio"),
          if (isSelected)
            Icon(
              isPriceAsc ? Icons.arrow_upward : Icons.arrow_downward,
              size: 18,
              color: Colors.green,
            ),
        ],
      ),
      selected: isSelected,
      onSelected: (selected) {
        setState(() {
          if (isSelected) {
            isPriceAsc = !isPriceAsc; // 🔄 cambia la dirección
          } else {
            sortOption = "precio";
            isPriceAsc = true;
          }
        });
      },
      backgroundColor: Colors.grey.shade200,
      selectedColor: Colors.green.shade100,
      checkmarkColor: Colors.green,
      labelStyle: TextStyle(fontWeight: FontWeight.w500),
      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(20)),
    );
  }
}
